site.headStyle:
/* Basic Reset */
* {
    box-sizing: border-box;
    margin: 10;
    padding: 10;
}

/* Styling the Form */
form {
    max-width: 600px;
    margin: 20px auto;
    padding: 20px;
    border: 1px solid #ddd;
    border-radius: 8px;
}

div {
    margin-bottom: 15px;
}

/* Label and Input Styles */
label {
    display: block;
    margin-bottom: 5px;
}

input[type="text"],
input[type="email"],
input[type="tel"],
input[type="number"] {
    width: 100%;
    padding: 8px;
    border: 1px solid #ccc;
    border-radius: 4px;
}

/* Radio Buttons Inline Display */
.radio-group label {
    display: inline-block;
    margin-right: 10px;
}

/* Checkbox and Submit Button */
input[type="checkbox"],
input[type="submit"] {
    margin-top: 10px;
}

input[type="submit"] {
    background-color: #555555;
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
}

input[type="submit"]:hover {
    background-color: #E60D32;
}

/* Responsive Design */
@media (max-width: 600px) {
    form {
        padding: 15px;
    }

    .radio-group label {
        display: block;
        margin-bottom: 5px;
    }
}

site.headScript:
function validateForm() {
    // Validate text fields
    var fields = ["name", "surname", "institute", "course", "graduationYear", "email", "phone"];
    for (var i = 0; i < fields.length; i++) {
        var field = document.forms["RegisterIndustryContest"][fields[i]].value;
        if (field == null || field == "") {
            alert("All fields must be filled out");
            return false;
        }
    }

    // Validate gender radio buttons
    if (!document.querySelector('input[name="gender"]:checked')) {
        alert("Please select a gender");
        return false;
    }

    // Validate checkboxes
    if (!document.forms["RegisterIndustryContest"]["acceptRegulationsAndPolicies"].checked) {
        alert("You must agree to the contest regulations and privacy policy");
        return false;
    }

    // Validate reCAPTCHA
    var recaptcha = grecaptcha.getResponse();
    if (recaptcha.length == 0) {
        alert("Please verify that you are not a robot");
        return false;
    }

    return true;
}
